import 'package:flutter/material.dart';import 'package:flutter_animate/flutter_animate.dart';import 'package:sizer/sizer.dart';import '../../../core/app_export.dart';/// Animated logo widget for the splash screen with mystical glow effectclass AnimatedLogoWidget extends StatefulWidget {  /// Duration of the animation  final Duration animationDuration;  /// Callback when animation completes  final VoidCallback? onAnimationComplete;  const AnimatedLogoWidget({    super.key,    this.animationDuration = const Duration(seconds: 2),    this.onAnimationComplete,  });  @override  State<AnimatedLogoWidget> createState() => _AnimatedLogoWidgetState();}class _AnimatedLogoWidgetState extends State<AnimatedLogoWidget>    with TickerProviderStateMixin {  late AnimationController _glowController;  late Animation<double> _glowAnimation;  @override  void initState() {    super.initState();    _initializeAnimations();    _startAnimations();  }  void _initializeAnimations() {    _glowController = AnimationController(      duration: const Duration(seconds: 2),      vsync: this,    );    _glowAnimation = Tween<double>(      begin: 0.3,      end: 1.0,    ).animate(CurvedAnimation(      parent: _glowController,      curve: Curves.easeInOut,    ));  }  void _startAnimations() {    _glowController.repeat(reverse: true);    // Complete animation after specified duration    Future.delayed(widget.animationDuration, () {      if (mounted && widget.onAnimationComplete != null) {        widget.onAnimationComplete!();      }    });  }  @override  void dispose() {    _glowController.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Center(      child: AnimatedBuilder(        animation: _glowAnimation,        builder: (context, child) {          return Container(            width: 35.w,            height: 35.w,            decoration: BoxDecoration(              shape: BoxShape.circle,              boxShadow: [                BoxShadow(                  color: AppTheme.lightTheme.primaryColor                      .withValues(alpha: _glowAnimation.value * 0.6),                  blurRadius: 30 * _glowAnimation.value,                  spreadRadius: 10 * _glowAnimation.value,                ),                BoxShadow(                  color: AppTheme.lightTheme.colorScheme.secondary                      .withValues(alpha: _glowAnimation.value * 0.4),                  blurRadius: 50 * _glowAnimation.value,                  spreadRadius: 5 * _glowAnimation.value,                ),              ],            ),            child: Container(              decoration: BoxDecoration(                shape: BoxShape.circle,                gradient: RadialGradient(                  colors: [                    AppTheme.lightTheme.primaryColor,                    AppTheme.lightTheme.colorScheme.secondary,                    AppTheme.lightTheme.primaryColor.withValues(alpha: 0.8),                  ],                  stops: const [0.0, 0.7, 1.0],                ),              ),              child: Center(                child: Column(                  mainAxisAlignment: MainAxisAlignment.center,                  children: [                    CustomIconWidget(                      iconName: 'auto_awesome',                      color: Colors.white,                      size: 12.w,                    ),                    SizedBox(height: 1.h),                    Text(                      'AstroPro',                      style: Theme.of(context).textTheme.titleLarge?.copyWith(                        color: Colors.white,                        fontWeight: FontWeight.bold,                        fontSize: 16.sp,                      ),                    ),                    Text(                      'Provider',                      style: Theme.of(context).textTheme.bodyMedium?.copyWith(                        color: Colors.white.withValues(alpha: 0.9),                        fontSize: 12.sp,                        letterSpacing: 1.2,                      ),                    ),                  ],                ),              ),            ),          );        },      ),    ).animate().fadeIn(duration: 800.ms, curve: Curves.easeOut).scale(      begin: const Offset(0.5, 0.5),      end: const Offset(1.0, 1.0),      duration: 1000.ms,      curve: Curves.elasticOut,    );  }}